import React from 'react'
import "./Card.css";

function Card({ id, title, tag, status, priority }) {
    const isStatus = localStorage.getItem("group") === "status";
    const isPriority = localStorage.getItem("group") === "priority";

    return (
        <div className='card'>
            <div className='cardHeading'>
                <span style={{ textTransform: "uppercase" }} className="color-grey">
                    {id}
                </span>
                <div
                    className="imageContainer relative"
                    style={{ width: "30px", height: "30px" }}
                >
                    <img
                        style={{ width: "95%", height: "95%", borderRadius: "50%" }}
                        src="data:image/jpeg;base64,"
                        alt="UserImage"
                    />
                    <div className="showStatus"></div>
                    </div>

            </div>
            <div className='cardTitle'>
                {!isStatus &&
                    (
                        status === "Backlog" ? (
                            <img src="../assets/Backlog.svg" alt="" />
                        ) :
                            status === "Todo" ? (
                                <img src="../assets/To_do.svg" alt="" />
                            ) :
                                status === "In progress" ? (
                                    <img src="../assets/in_progress.svg" alt="" />
                                ) :
                                    status === "Done" ? (
                                        <img src="../assets/Done.svg" alt="" />
                                    ) :
                                        (
                                            <img src="../assets/Cancelled.svg" alt="" />
                                        )
                    )}
                <span style={{ margin: "0.2px" }}>{title}</span>
            </div>
            <div className='cardTags'>
                {!isPriority &&
                    (priority === 0 ? (
                        <img src="../assets/No_priority.svg" alt="" />
                    ) :
                        priority === 1 ? (
                            <img src="../assets/Img_Low_Priority.svg" alt="" />
                        ) :
                            priority === 2 ? (
                                <img src="../assets/Img_Medium_Priority.svg" alt="" />
                            ) :
                                priority === 3 ? (
                                    <img src="../assets/Img_High_Priority.svg" alt="" />
                                ) :
                                    priority === 4 ? (
                                        <img src="../assets/Urgent_Priority_grey.svg" alt="" />
                                    ) :
                                        (
                                            <p>.</p>
                                        )
                    )}
                {tag?.map((element, index) => {
                    return (
                        <div key={index} className="tags color-grey">
                            {" "}
                            <span>•</span> {element}
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default Card;
